sqlMap.select:
/* selectAdminDataList */ SELECT io_data.*, u.users_nm
FROM (SELECT file_data.*, SUM(file_ath_data.file_size) as file_total_size, file_ath_data.file_nm as file_nm, COUNT(*) as file_total_count, MAX(file_ath_data.user_download_time) as max_download_time FROM as file_data , as file_ath_data WHERE file_data.data_seq = file_ath_data.data_seq
GROUP BY data_seq )AS io_data INNER JOIN (SELECT users_id AS id, users_nm, dept_seq FROM c_user_mgt user WHERE rst_seq = 0 AND cud_cd != 'D'
) u ON (u.id = io_data.users_id) INNER JOIN (SELECT dept_seq , dept_nm FROM c_dept_mgt WHERE rst_seq = 0 AND cud_cd != 'D') m ON ( m.dept_seq = u.dept_seq )
WHERE 1=1
GROUP BY io_data.data_seq ORDER BY io_data.crt_time DESC limit $offSet$, $pageListSize$

/* selectDataListCount */ SELECT COUNT(*) FROM (SELECT file_data.* FROM as file_data, as file_ath_data WHERE file_data.data_seq = file_ath_data.data_seq
GROUP BY data_seq )AS io_data INNER JOIN (SELECT users_id AS id, users_nm, dept_seq FROM c_user_mgt user WHERE rst_seq = 0 AND cud_cd != 'D'
) u ON (u.id = io_data.users_id) INNER JOIN (SELECT dept_seq , dept_nm FROM c_dept_mgt WHERE rst_seq = 0 AND cud_cd != 'D') m ON ( m.dept_seq = u.dept_seq )
WHERE 1=1

/* selectDataExcelList */ SELECT io_data.*, u.users_nm as users_nm, #networkPosition# as networkPosition, , IFNULL(appr_users_id, 'N') AS appr_id, IF(appr_users_id != '' AND appr_users_id IS NOT NULL, (SELECT appr_info.users_nm FROM c_user_mgt appr_info WHERE rst_seq = 0 AND appr.appr_users_id = appr_info.users_id), '') AS appr_nm, app_type AS appr_type, app_time AS appr_time FROM (SELECT file_data.* , file_ath_data.file_nm, file_ath_data.file_size, file_ath_data.encrypt_yn, file_ath_data.user_download_time FROM as file_data , as file_ath_data WHERE file_data.data_seq = file_ath_data.data_seq
)AS io_data INNER JOIN (SELECT users_id, users_nm, dept_seq FROM c_user_mgt user WHERE rst_seq = 0 AND cud_cd != 'D'
) u ON (u.users_id = io_data.users_id) INNER JOIN (SELECT dept_seq, dept_nm FROM c_dept_mgt WHERE rst_seq = 0 AND cud_cd != 'D') m ON ( m.dept_seq = u.dept_seq )
LEFT JOIN (SELECT IFNULL(real_appr_id, appr_id) AS appr_users_id, np_cd, data_seq, app_type, app_time FROM approval WHERE app_ord = 1 ) appr ON (appr.np_cd = #networkPosition# AND appr.data_seq = io_data.data_seq) WHERE 1=1
ORDER BY crt_time DESC

/* selectDataTotalExcelList */ SELECT i_o_data.*, u.users_nm as users_nm, , IFNULL(appr_users_id, 'N') AS appr_id, IF(appr_users_id != '' AND appr_users_id IS NOT NULL, (SELECT appr_info.users_nm FROM c_user_mgt appr_info WHERE rst_seq = 0 AND appr.appr_users_id = appr_info.users_id), '') AS appr_nm, app_type AS appr_type, app_time AS appr_time FROM ( (SELECT 'I' as networkPosition, io_data.title, io_data.status, io_data.dept_nm_past, io_data.dept_nm_path, i_a.* FROM in_data as io_data , in_ath_file as i_a WHERE 1=1 AND io_data.data_seq = i_a.data_seq
) UNION ALL ( SELECT 'O' as networkPosition, io_data.title, io_data.status, io_data.dept_nm_past, io_data.dept_nm_path, o_a.* FROM out_data as io_data , out_ath_file as o_a WHERE 1=1 AND io_data.data_seq = o_a.data_seq
) ) as i_o_data INNER JOIN (SELECT users_id, users_nm, dept_seq FROM c_user_mgt user WHERE rst_seq = 0 AND cud_cd != 'D'
) u ON (u.users_id = i_o_data.users_id) INNER JOIN (SELECT dept_seq, dept_nm FROM c_dept_mgt WHERE rst_seq = 0 AND cud_cd != 'D') m ON ( m.dept_seq = u.dept_seq )
LEFT JOIN (SELECT IFNULL(real_appr_id, appr_id) AS appr_users_id, np_cd, data_seq, app_type, app_time FROM approval WHERE app_ord = 1 ) appr ON (appr.np_cd = i_o_data.networkPosition AND appr.data_seq = i_o_data.data_seq) WHERE 1=1
ORDER BY i_o_data.crt_time DESC

/* selectDataTotalListCount */
SELECT COUNT(*) FROM ( (SELECT i.* FROM in_data as i, in_ath_file as i_ath WHERE 1=1 AND i.data_seq = i_ath.data_seq
GROUP BY data_seq ) UNION ALL (SELECT o.* FROM out_data as o, out_ath_file as o_ath WHERE 1=1 AND o.data_seq = o_ath.data_seq
GROUP BY data_seq ))AS io_data INNER JOIN (SELECT users_id as id, users_nm, dept_seq FROM c_user_mgt user WHERE RST_SEQ = 0 AND cud_cd != 'D'
) u ON (u.id = io_data.users_id) INNER JOIN (SELECT dept_seq, dept_nm FROM c_dept_mgt WHERE RST_SEQ = 0 AND cud_cd != 'D') m ON ( m.dept_seq = u.dept_seq )
WHERE 1=1

/* Data.selectVcFileTotalList */ SELECT b.*, u.users_nm, d.dept_seq , d.dept_nm FROM ( ( SELECT a.*, 'I' as np_cd FROM in_ath_file a WHERE 1=1
apt_status = 0
) UNION ALL ( SELECT a.*, 'O' as np_cd FROM out_ath_file a WHERE 1=1
apt_status = 0
) ) b, c_user_mgt u, c_dept_mgt d WHERE b.crtr_id = u.users_id AND u.rst_seq = 0 and d.rst_seq = 0 and u.dept_seq = d.dept_seq
b.$searchField$ LIKE CONCAT('%',#filterSearchValue#,'%')
u.$searchField$ LIKE CONCAT('%',#filterSearchValue#,'%')
ORDER BY b.crt_time DESC limit $offSet$, $pageListSize$

/* Data.selectVcFileListTotalCount */ SELECT COUNT(c.data_seq) FROM ( SELECT b.* , u.users_nm FROM ( ( SELECT a.* FROM in_ath_file a WHERE 1=1
apt_status = 0
) UNION ALL ( SELECT a.* FROM out_ath_file a WHERE 1=1
apt_status = 0
) ) b, c_user_mgt u WHERE b.crtr_id = u.users_id AND u.rst_seq = 0
b.$searchField$ LIKE CONCAT('%',#filterSearchValue#,'%')
u.$searchField$ LIKE CONCAT('%',#filterSearchValue#,'%')
) AS c WHERE 1=1

/* selectDlpFileListTotalCount */ SELECT COUNT(c.data_seq) FROM ( SELECT b.* , u.users_nm FROM ( ( SELECT a.* FROM in_ath_file a WHERE 1=1 AND dlp_status = #dlp_status#
) UNION ALL ( SELECT a.* FROM out_ath_file a WHERE 1=1 AND dlp_status = #dlp_status#
) ) b, c_user_mgt u , c_dept_mgt d WHERE b.crtr_id = u.users_id AND u.rst_seq = 0 AND u.dept_seq = d.dept_seq AND d.rst_seq = 0
) AS c WHERE 1=1

/* selectAptFileListTotalCount */ SELECT COUNT(c.data_seq) FROM ( SELECT b.* , u.users_nm FROM ( ( SELECT a.* FROM in_ath_file a WHERE 1=1 AND apt_status = #apt_status#
) UNION ALL ( SELECT a.* FROM out_ath_file a WHERE 1=1 AND apt_status = #apt_status#
) ) b, c_user_mgt u , c_dept_mgt d WHERE b.crtr_id = u.users_id AND u.rst_seq = 0 AND u.dept_seq = d.dept_seq AND d.rst_seq = 0
) AS c WHERE 1=1

/* selectAthFileCnt */ SELECT count(*) FROM in_ath_file
out_ath_file
WHERE 1=1 AND data_seq = #data_seq#

/* selectAttachFileMetaList */ SELECT data_seq, count(IF(vc_status=0,1,NULL)) AS scanfail_cnt , count(IF(vc_status=1,1,NULL)) AS clean_cnt , count(IF(vc_status=3 OR vc_status=4,1,NULL)) infected_cnt , count(IF(vc_status=10,1,NULL)) AS password_cnt , count(IF(vc_status=11,1,NULL)) AS forgery_cnt , count(IF(vc_status=12,1,NULL)) AS filtering_cnt , count(IF(vc_status=13,1,NULL)) AS approval_cnt , count(IF(vc_status=14,1,NULL)) AS compress_cnt , count(IF(dlp_status=1,1,NULL)) AS dlp_cnt , count(IF(apt_status=1 OR (apt_status > 1 AND apt_send_yn = 'N'),1,NULL)) AS apt_cnt , SUM(file_size) size, count(data_seq) as total_cnt FROM
#datas[].data_seq#
GROUP BY data_seq

sqlMap.delete:
/* data.deleteReceiveData */
DELETE FROM receive_data
WHERE data_seq = #data_seq# AND io_cd = #io_cd#

sqlMap.select:
/* Data.selectDownloadableDataAttachFile */ SELECT ath_ord, file_nm, vc_status FROM d, a WHERE d.data_seq = #data_seq# AND d.data_seq = a.data_seq AND ( SELECT CONCAT(',', common_value, ',') FROM c_common_code WHERE common_cd = 'NOT_NORMAL' ) NOT LIKE CONCAT('%,', a.VC_STATUS,',%')
out_del_yn = 'N'
in_del_yn = 'N'
